	SetCharacterGoods(rChar, GOOD_KNIPPELS, 300);
	SetCharacterGoods(rChar, GOOD_BOMBS, 500);
	SetCharacterGoods(rChar, GOOD_FOOD, 500);
	SetCharacterGoods(rChar, GOOD_POWDER, 800);
	SetCharacterGoods(rChar, GOOD_WEAPON, 300);
