	FantomMakeCoolFighter(sld, 30, 50, 50, "blade_16", "pistol4", "bullet", 200);
	sld.dialog.FileName = "Quest\LSC\OtherNPC.c";
	sld.dialog.currentnode = "Prisonboss";
	sld.greeting = "town_pirate";
