			break;
			
			case 4:
				iShip = SHIP_FRIGATE;
				iCannon = CANNON_TYPE_CANNON_LBS24;
				sName = "Лоял Лондон";
			break;
			
			case 5:
				iShip = SHIP_POLACRE;
				iCannon = CANNON_TYPE_CANNON_LBS20;
