    sld.Dialog.Filename = "Quest\LineMiniQuests\FalseTrace.c";
	sld.dialog.currentnode = "FalseTrace";
	sld.greeting = "town_pirate";
    sld.loyality = 25;
	sld.rank 	= 20;
    sld.reputation = 10;
	sld.alignment = "bad";
	LAi_SetHP(sld, 100+MOD_SKILL_ENEMY_RATE*35, 100+MOD_SKILL_ENEMY_RATE*35);
