	if (!CheckAttribute(pchar, "questTemp.Caleuche.Garpiya")) return;

	if (pchar.questTemp.Garpiya == "to_portroyal") pchar.questTemp.Garpiya = "to_marigo";
	else pchar.questTemp.Garpiya = "to_portroyal";
