		break;
		
		case 1:
			sColony = "Shore44";
			iDay = 6;
		break;
		
		case 2:
			sColony = "Mayak8";
			iDay = 8;
		break;
		
		case 3:
			sColony = "Shore58";
			iDay = 10;
		break;
		
		case 4:
			sColony = "Shore2";
			iDay = 5;
		break;
		
		case 5:
			sColony = "Shore28";
			iDay = 8;
